import React, { useEffect, useState, useRef } from "react";
import { useParams, useNavigate } from "react-router-dom";
import io from "socket.io-client";

const socket = io("http://localhost:3000");

const ChatPage = () => {
  const { username } = useParams();
  const navigate = useNavigate();

  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);

  const [typingUser, setTypingUser] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const typingTimeoutRef = useRef(null);

  // incoming messages
  useEffect(() => {
    socket.on("message", (data) => {
      setMessages((prev) => [...prev, data]);
    });

    return () => {
      socket.off("message");
    };
  }, []);

  // typing events from other users
  useEffect(() => {
    socket.on("typing", (name) => {
      setTypingUser(name);
    });

    socket.on("stopTyping", () => {
      setTypingUser("");
    });

    return () => {
      socket.off("typing");
      socket.off("stopTyping");
    };
  }, []);

  const handleInputChange = (e) => {
    setMessage(e.target.value);

    if (!isTyping) {
      socket.emit("typing", username);
      setIsTyping(true);
    }

    // reset the timer every time user types
    if (typingTimeoutRef.current) {
      clearTimeout(typingTimeoutRef.current);
    }

    typingTimeoutRef.current = setTimeout(() => {
      socket.emit("stopTyping");
      setIsTyping(false);
      typingTimeoutRef.current = null;
    }, 1000);
  };

  const sendMessage = (e) => {
    e.preventDefault();
    if (!message.trim()) return;

    const msgData = {
      username,
      text: message,
      time: new Date().toLocaleTimeString(),
    };

    socket.emit("message", msgData);
    setMessage("");

    // when message is sent, user stops typing
    if (typingTimeoutRef.current) {
      clearTimeout(typingTimeoutRef.current);
      typingTimeoutRef.current = null;
    }
    socket.emit("stopTyping");
    setIsTyping(false);
  };

  const handleLogout = () => {
    navigate("/");
  };

  return (
    <>
      <a className="logout-link" onClick={handleLogout}>
        Logout
      </a>

      <div className="header">
        <h2>mayankCHAT</h2>
        <p>Chatting as {username}</p>
      </div>

      {/* typing indicator (don’t show it for yourself) */}
      {typingUser && typingUser !== username && (
        <div className="typing-indicator">
          {typingUser} is typing…
        </div>
      )}

      <div className="chat-container">
        {messages.map((m, i) => {
          const mine = m.username === username;
          return (
            <div key={i} className={mine ? "my-chat" : "notmy-chat"}>
              <span className="user">
                {m.username} • {m.time}
              </span>
              <span className="msg">{m.text}</span>
            </div>
          );
        })}
      </div>

      <div className="chatbox-container">
        <form className="chatbox" onSubmit={sendMessage}>
          <input
            type="text"
            placeholder="Type a message..."
            value={message}
            onChange={handleInputChange}
          />
          <button type="submit">Send</button>
        </form>
      </div>
    </>
  );
};

export default ChatPage;
